    private void ConvertirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ConvertirActionPerformed
        // TODO add your handling code here:
        
        Double cantidad;
        Double pies;
        Double yardas;
        Double metros;
        Double centimetros;
        
        cantidad = Double.parseDouble(valorCantidad.getText());
        
        pies = cantidad/12;
        yardas = pies/3;
        centimetros = cantidad*2.52;
        metros = centimetros/100;
        
        resultadoPies.setText(String.valueOf(pies));
        resultadoYardas.setText(String.valueOf(yardas));
        resultadoCentimetros.setText(String.valueOf(centimetros));
        resultadoMetros.setText(String.valueOf(metros));
        
    }//GEN-LAST:event_ConvertirActionPerformed

